package ab52;

public class DatatypesVariable 
{

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		//Declare int datatype
		int a = 10;
		System.out.println(a);
		
		//Declare byte datatype
		byte b = 20;
		System.out.println(b);
		
		//declare short datatype
		short c = 1;
		System.out.println(c);
		
		//declare long datatype
		long d = 233454643;
		System.out.println(d);
		
		//declare float datatype
		float e = 3.14f;
		System.out.println(e);
		
		//decalre double datatype
		double f = 42335.345;
		System.out.println(f);
		
		//declare char datatype
		char g = 'A';
		System.out.println(g);
		
		//declare boolean datatype
		boolean h = true;
		System.out.println(h);
		
		//declare string datatype
		String i = "Declare and Initialize variables of its datatypes";
		System.out.println(i);
		
		}

}
